mapper.select:
select
  other_insurance_information_id, other_insurance_information_date_begin, other_insurance_information_date_finish, other_insurance_information_name, other_insurance_information_card, other_insurance_information_email
from insurance.insurance_other_insurance_information


        and other_insurance_information_id = #{otherInsuranceInformationId}


        and other_insurance_information_date_begin = #{otherInsuranceInformationDateBegin}


        and other_insurance_information_date_finish = #{otherInsuranceInformationDateFinish}


        and other_insurance_information_name = #{otherInsuranceInformationName}


        and other_insurance_information_card = #{otherInsuranceInformationCard}


        and other_insurance_information_email = #{otherInsuranceInformationEmail}
